mapper.select:
select

from T_LIQUIDATION_SUB_ORDER
where 1=1

  and ID = #{id,jdbcType=VARCHAR}


  and LIQUIDATION_ORDER_ID = #{liquidationOrderId,jdbcType=VARCHAR}


  and PAY_SUB_ORDER_ID = #{paySubOrderId,jdbcType=VARCHAR}


  and PAY_METHOD = #{payMethod,jdbcType=VARCHAR}


  and PAY_STATUS = #{payStatus,jdbcType=VARCHAR}


  and TRANS_CURRENCY = #{transCurrency,jdbcType=VARCHAR}


  and PAY_KIND = #{payKind,jdbcType=VARCHAR}


  and CARD_COUNTRY = #{cardCountry,jdbcType=VARCHAR}


  and CARD_TYPE = #{cardType,jdbcType=VARCHAR}


  and TRANS_METHOD = #{transMethod,jdbcType=VARCHAR}


  and TRANS_MODEL = #{transModel,jdbcType=VARCHAR}


  and CARD_NO = #{cardNo,jdbcType=VARCHAR}


  and PAY_ORG_CODE = #{payOrgCode,jdbcType=VARCHAR}


  and PAY_CURRENCY = #{payCurrency,jdbcType=VARCHAR}


  and PAY_AMOUNT = #{payAmount,jdbcType=DECIMAL}


  and PAY_COMPLETE_TIME = #{payCompleteTime,jdbcType=TIMESTAMP}


  and ACCOUNTING_STATUS = #{accountingStatus,jdbcType=VARCHAR}


  and CHANNEL_ORDER_NO = #{channelOrderNo,jdbcType=VARCHAR}

order by GMT_CREATE_TIME desc
