mapper.select:
select

from T_CHANNEL_ORDER
where 1=1

  and ID = #{id,jdbcType=VARCHAR}


  and MERCHANT_ID = #{merchantId,jdbcType=VARCHAR}


  and MERCHANT_NAME = #{merchantName,jdbcType=VARCHAR}


  and TRANS_TYPE = #{transType,jdbcType=VARCHAR}


  and TRANS_STATUS = #{transStatus,jdbcType=VARCHAR}


  and PAY_METHOD = #{payMethod,jdbcType=VARCHAR}


  and CHANNEL_TYPE = #{channelType,jdbcType=VARCHAR}


  and ORG_CODE = #{orgCode,jdbcType=VARCHAR}


  and ORG_SUB_CODE = #{orgSubCode,jdbcType=VARCHAR}


  and PAY_KIND = #{payKind,jdbcType=VARCHAR}


  and PAY_CURRENCY = #{payCurrency,jdbcType=VARCHAR}


  and PAY_AMOUNT = #{payAmount,jdbcType=DECIMAL}


  and MERCHANT_ORDER_ID = #{merchantOrderId,jdbcType=VARCHAR}


  and ORDER_ID = #{orderId,jdbcType=VARCHAR}


  and PAYMENT_ORDER_ID = #{paymentOrderId,jdbcType=VARCHAR}


  and CHANNEL_ORDER_ID = #{channelOrderId,jdbcType=VARCHAR}


  and CHANNEL_COMPLETE_TIME = #{channelCompleteTime,jdbcType=TIMESTAMP}


  and CHANNEL_RETURN_ID = #{channelReturnId,jdbcType=VARCHAR}


  and CHARGE_COST_TYPE = #{chargeCostType,jdbcType=VARCHAR}


  and CHARGE_COST_FLAG = #{chargeCostFlag,jdbcType=VARCHAR}


  and CHANNEL_REFERENCE_NO = #{channelReferenceNo,jdbcType=VARCHAR}


  and CHANNEL_ACCESS_CODE = #{channelAccessCode,jdbcType=VARCHAR}


  and ORI_CHANNEL_ORDER_ID = #{oriChannelOrderId,jdbcType=VARCHAR}


  and ORI_PAY_CURRENCY = #{oriPayCurrency,jdbcType=VARCHAR}


  and ORI_PAY_AMOUNT = #{oriPayAmount,jdbcType=DECIMAL}

select * from t_channel_order where 1 = 1

    and merchant_id = #{merchantId}


    and trans_type = #{transType}


    and trans_status = #{transStatus}


    and org_code = #{orgCode}


    and org_sub_code = #{orgSubCode}


    and pay_currency = #{payCurrency}


    and pay_method = #{payMethod}


    and channel_order_id = #{channelOrderId}


    and merchant_order_id = #{merchantOrderId}


    and channel_complete_time >= #{beginChannelCompleteTime}


    and channel_complete_time <= #{endChannelCompleteTime}

order by gmt_create_time desc

  select co.org_code,
     co.org_sub_code,
     co.channel_order_id,
     co.pay_method,
     co.trans_type,
     co.trans_status,
     co.pay_currency,
     co.pay_amount,
     ccd.percent_fee_amount,
     ccd.fixed_fee_currency,
     ccd.fixed_fee_amount,
     ccd.fee_currency,
     ccd.fee_amount,
     ccd.charge_cost_method,
     ccd.cost_settle_currency,
     ccd.cost_settle_date,
     co.channel_complete_time
from t_channel_order co
left join t_channel_clearing_detail ccd on co.channel_order_id = ccd.channel_detail_id where 1 = 1
 and co.org_code = #{orgCode} 
 and co.org_sub_code = #{orgSubCode} 
 and co.pay_method = #{payMethod} 
 and co.trans_type = #{transType} 
 and co.trans_status = #{transStatus} 
 and co.pay_currency = #{payCurrency} 
 and ccd.charge_cost_method = #{chargeCostMethod} 
 and co.channel_complete_time >= #{beginChannelCompleteTime} 
 and co.channel_complete_time <= #{endChannelCompleteTime} 
 and ccd.cost_settle_date >= #{beginCostSettleDate} 
 and ccd.cost_settle_date <= #{endCostSettleDate} 
 and co.channel_order_id = #{channelOrderId} 
order by co.gmt_create_time desc
