mapper.select:
select

from T_LIQUIDATION_ORDER
where 1=1

  and ID = #{id,jdbcType=VARCHAR}


  and MERCHANT_ID = #{merchantId,jdbcType=VARCHAR}


  and MERCHANT_NAME = #{merchantName,jdbcType=VARCHAR}


  and PAY_METHOD = #{payMethod,jdbcType=VARCHAR}


  and PRODUCT_CODE = #{productCode,jdbcType=VARCHAR}


  and TRANS_TYPE = #{transType,jdbcType=VARCHAR}


  and TRANS_STATUS = #{transStatus,jdbcType=VARCHAR}


  and TRANS_CURRENCY = #{transCurrency,jdbcType=VARCHAR}


  and TRANS_AMOUNT = #{transAmount,jdbcType=DECIMAL}


  and SETTLE_CURRENCY = #{settleCurrency,jdbcType=VARCHAR}


  and SETTLE_DATE = #{settleDate,jdbcType=VARCHAR}


  and SUB_ACCOUNT_DATA = #{subAccountData,jdbcType=VARCHAR}


  and STATUS = #{status,jdbcType=VARCHAR}


  and FEE_TYPE = #{feeType,jdbcType=VARCHAR}


  and FEE_ID = #{feeId,jdbcType=VARCHAR}


  and FEE_FLAG = #{feeFlag,jdbcType=VARCHAR}


  and MERCHANT_ORDER_ID = #{merchantOrderId,jdbcType=VARCHAR}


  and ORDER_ID = #{orderId,jdbcType=VARCHAR}


  and PAY_ORDER_ID = #{payOrderId,jdbcType=VARCHAR}


  and ORI_MERCHANT_ORDER_ID = #{oriMerchantOrderId,jdbcType=VARCHAR}


  and ORI_ORDER_ID = #{oriOrderId,jdbcType=VARCHAR}


  and ORI_PAY_ORDER_ID = #{oriPayOrderId,jdbcType=VARCHAR}


  and PAY_COMPLETE_TIME = #{payCompleteTime,jdbcType=TIMESTAMP}


  and PAY_METHOD = #{payMethod,jdbcType=VARCHAR}


  and TRANS_METHOD = #{transMethod,jdbcType=VARCHAR}


  and PAY_ORG_CODE = #{payOrgCode,jdbcType=VARCHAR}


  and IS_ALL_REFUND =#{isAllRefund,jdbcType=VARCHAR}


  and  CALC_FEE_ID=#{calcFeeId,jdbcType=VARCHAR}

select  from t_liquidation_order where 1=1
 and merchant_id = #{merchantId} 
 and trans_type = #{transType} 
 and trans_status = #{transStatus} 
 and trans_currency = #{transCurrency} 
 and merchant_order_id = #{merchantOrderId} 
 and pay_order_id = #{payOrderId} 
 and order_id = #{orderId} 
 and pay_method = #{payMethod} 
 and pay_complete_time >= #{beginPayCompleteTime} 
 and pay_complete_time <= #{endPayCompleteTime} 
order by gmt_create_time desc
