mapper.select:
select

from T_RISK_ORDER
where 1=1

  and ID = #{id,jdbcType=VARCHAR}


  and MERCHANT_ID = #{merchantId,jdbcType=VARCHAR}


  and MERCHANT_NAME = #{merchantName,jdbcType=VARCHAR}


  and MERCHANT_ORDER_ID = #{merchantOrderId,jdbcType=VARCHAR}


  and ORDER_ID = #{orderId,jdbcType=VARCHAR}


  and RISK_ORDER_ID = #{riskOrderId,jdbcType=VARCHAR}


  and TRANS_CURRENCY = #{transCurrency,jdbcType=VARCHAR}


  and TRANS_AMOUNT = #{transAmount,jdbcType=DECIMAL}


  and FEE_TYPE = #{feeType,jdbcType=VARCHAR}


  and FEE_ID = #{feeId,jdbcType=VARCHAR}


  and FEE_FLAG = #{feeFlag,jdbcType=VARCHAR}


  and SETTLE_CURRENCY = #{settleCurrency,jdbcType=VARCHAR}


  and SETTLE_AMOUNT = #{settleAmount,jdbcType=DECIMAL}


  and TRANS_TYPE = #{transType,jdbcType=VARCHAR}


  and PAY_METHOD = #{payMethod,jdbcType=VARCHAR}
